\begin{problem}[Composition]
Let $f$ and $g$ be functions that have formulas $f(x)=2^x$ and $g(x)=3^x$. Find each 
of the following.
\begin{multicols}{3}
\begin{subproblem}[core]
$(f\circ g)(1)$
\begin{shortsolution}
$8$ 
\end{shortsolution}
\end{subproblem}
\begin{subproblem}[core]
$(g\circ f)(2)$
\begin{shortsolution}
$81$ 
\end{shortsolution}
\end{subproblem}
\begin{subproblem}[core]
$(f\circ g)(0)$
\begin{shortsolution}
$2$ 
\end{shortsolution}
\end{subproblem}
\begin{subproblem}
$(g\circ f)(0)$
\begin{shortsolution}
$3$ 
\end{shortsolution}
\end{subproblem}
\begin{subproblem}
$(f\circ g)(x)$
\begin{shortsolution}
$2^{3^x}$ 
\end{shortsolution}
\end{subproblem}
\begin{subproblem}
$(g\circ f)(x)$
\begin{shortsolution}
$3^{2^x}$ 
\end{shortsolution}
\end{subproblem}
\end{multicols}
\end{problem}
