\begin{tikzpicture}
\begin{axis}[
framed,
xmin=-10,xmax=50,
ymin=-20,ymax=100,
xtick={-30}, ytick={-30},
minor xtick={10,20,...,50},
minor ytick={20,40,...,100},
xlabel={$t$},
grid=minor,
]
\addplot+[->]expression[domain=0:50,samples=50]{40*exp(-x/10)+50};
\end{axis}
\end{tikzpicture}

